import './Slide.css';

const Slide = ({ slideId, currentSlideId, children }) => (
    <div className="slide"
        style={{
            opacity: slideId === currentSlideId ? 1 : 0,
            position: "absolute"
        }}>
            <div>
                {children}
            </div>
    </div>
);
    
export default Slide;
