import { useState } from 'react';
import { useSwipeable } from 'react-swipeable';

import title from '../assets/title.png';
import gift from '../assets/gift.gif';
import swipe from '../assets/swipe-left.gif';

import Slide from './slide/Slide';
import './App.css';

const App = () => { 
  const [currentSlideId, setCurrentSlideId] = useState(0);

  const handlers = useSwipeable({
    onSwiped: (eventData) => {
      console.log("User Swiped!", eventData)
      if (eventData.dir === "Left") {
        setCurrentSlideId((prev) => prev + 1 > 7 ? 7 : prev + 1)
      } else {
        setCurrentSlideId((prev) => prev - 1 === 0 ? 0 : prev - 1)
      }
    }
  });

  return (
    <div className="app" {...handlers}>
      <Slide slideId={0} currentSlideId={currentSlideId}>
        0
          {/* <header>
            <img src={title} alt="joyeux noel" />
          </header>
          <p>Un Cadeau vous attend !</p>
          <p>Qu'est ce que c'est ???</p>
          <img src={gift} alt="christmas gift"/>
          <p>
            swipe left!
          </p>
          <img src={swipe} alt="swipe left"/> */}
      </Slide>
      <Slide slideId={1} currentSlideId={currentSlideId}>
        1
      </Slide>
      <Slide slideId={2} currentSlideId={currentSlideId}>
        2
      </Slide>
      <Slide slideId={3} currentSlideId={currentSlideId}>
        3
      </Slide>
      <Slide slideId={4} currentSlideId={currentSlideId}>
        4
      </Slide>
      <Slide slideId={5} currentSlideId={currentSlideId}>
        5
      </Slide>
      <Slide slideId={6} currentSlideId={currentSlideId}>
        6
      </Slide>
      <Slide slideId={7} currentSlideId={currentSlideId}>
        7
      </Slide>
    </div>
  );
}

export default App;
